mapper.select:
select user_idx, user_id, user_pw, user_name, user_postal_code, user_addr1, user_addr2
from user_table
where user_idx = #{user_idx}

mapper.update:
update user_table
set user_pw = #{user_pw}, user_postal_code = #{user_postal_code}, user_addr1 = #{user_addr1}, user_addr2 = #{user_addr2}
where user_idx = #{user_idx}
